//IMPORTS
import React, { useEffect, useState } from "react";
import { Connection, PublicKey, clusterApiUrl } from "@solana/web3.js";
import { Program, Provider, web3 } from "@project-serum/anchor";
import idl from "./idl.json";
import kp from "./keypair.json";

// JSBarcode wrapped in React
import Barcode from "react-barcode";

// Toast Notifs
import toast, { Toaster } from "react-hot-toast";

// For card carousel
import Carousel from "./Carousel";
import "./Carousel.css";

// Main CSS
import "./App.css";
import "./Marquee.css";

// Bootstrap CSS
import "bootstrap/dist/css/bootstrap.min.css";
// Bootstrap Bundle JS
import "bootstrap/dist/js/bootstrap.bundle.min";

//React Icons - Material Design
import { MdCardMembership, MdCardGiftcard } from "react-icons/md";

//CONSTANTS
const { SystemProgram, Keypair } = web3;
const arr = Object.values(kp._keypair.secretKey);
const secret = new Uint8Array(arr);
const baseAccount = web3.Keypair.fromSecretKey(secret);
const programID = new PublicKey("72z5anaRG11E11uWemh8Ucer9gz23jiCaSPew4q938dR");
const network = clusterApiUrl("devnet");
const opts = {
  preflightCommitment: "processed",
};

const App = () => {
  //useSTATE
  const [walletAddress, setWalletAddress] = useState(null);
  const [inputValue, setInputValue] = useState("");
  const [gifList, setGifList] = useState([]);

  //TOASTS

  const showPhantomToast = () =>
    toast("To sign in, download a Phantom Wallet ðŸ‘» at https://phantom.app");
  const showConnectedWalletToast = () => toast.success("You're signed in!");
  const showDisconnectedWalletToast = () => toast.success("You've signed out!");
  const showGifSentToast = () => toast.success("GIF Sent!");

  //ACTIONS

  const checkIfWalletIsConnected = async () => {
    try {
      const { solana } = window;

      if (solana) {
        if (solana.isPhantom) {
          console.log("Phantom wallet found!");

          const response = await solana.connect({ onlyIfTrusted: true });
          console.log(
            "Connected with Public Key:",
            response.publicKey.toString()
          );
          setWalletAddress(response.publicKey.toString());
        }
      } else {
        showPhantomToast();
      }
    } catch (error) {
      console.error(error);
    }
  };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log("Connected with Public Key:", response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
      showConnectedWalletToast();
    }
  };

  const disconnectWallet = () => {
    console.log("Wallet Disconnected");
    setWalletAddress(null);
    showDisconnectedWalletToast();
  };

  const onInputChange = (event) => {
    const { value } = event.target;
    setInputValue(value);
  };

  const getProgram = async () => {
    const idl = await Program.fetchIdl(programID, getProvider());
    return new Program(idl, programID, getProvider());
  };

  const getProvider = () => {
    const connection = new Connection(network, opts.preflightCommitment);
    const provider = new Provider(
      connection,
      window.solana,
      opts.preflightCommitment
    );
    return provider;
  };

  const createGifAccount = async () => {
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);
      console.log("ping");
      await program.rpc.startStuffOff({
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
          systemProgram: SystemProgram.programId,
        },
        signers: [baseAccount],
      });
      console.log(
        "Created a new BaseAccount w/ address:",
        baseAccount.publicKey.toString()
      );
      await getGifList();
    } catch (error) {
      console.log("Error creating BaseAccount account:", error);
    }
  };

  const shortenAddress = (address) => {
    if (!address) return "";
    return address.substring(0, 4) + "....." + address.substring(40);
  };

  const getGifList = async () => {
    try {
      const program = await getProgram();
      const account = await program.account.baseAccount.fetch(
        baseAccount.publicKey
      );

      console.log("Got the account", account);
      setGifList(account.gifList);
    } catch (error) {
      console.log("Error in getGifList: ", error);
      setGifList(null);
    }
  };

  const sendGif = async () => {
    if (inputValue.length === 0) {
      console.log("No gif link given!");
      return;
    }
    setInputValue("");
    console.log("Gif link:", inputValue);
    try {
      const provider = getProvider();
      const program = new Program(idl, programID, provider);

      await program.rpc.addGif(inputValue, {
        accounts: {
          baseAccount: baseAccount.publicKey,
          user: provider.wallet.publicKey,
        },
      });
      console.log("GIF successfully sent to program", inputValue);

      await getGifList();
      showGifSentToast();
    } catch (error) {
      console.log("Error sending GIF:", error);
    }
  };

  const Card = ({ title, content }) => (
    <div className="card">
      <h2>{title}</h2>
      {content}
    </div>
  );

  const renderNotConnectedContainer = () => (
    <div className="container">
      <nav className="navbar fixed-top navbar-expand-lg">
        <div className="container-fluid">
          <p className="header" href="#">
            CODE128
          </p>
          <button
            className="cta-button connect-wallet-button"
            onClick={connectWallet}
          >
            SIGN IN
          </button>
        </div>
      </nav>
      <article className="wrapper">
        <div className="marquee">
          <div className="marquee__group">
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
          </div>

          <div aria-hidden="true" className="marquee__group">
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
          </div>
        </div>

        <div className="marquee marquee--reverse">
          <div className="marquee__group">
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard />
            </span>
          </div>

          <div aria-hidden="true" className="marquee__group">
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
            <span>
              <MdCardGiftcard className="cover-icon"/>
            </span>
          </div>
        </div>
      </article>
    </div>
  );

  const renderConnectedContainer = () => {
    if (gifList === null) {
      return (
        <div className="container connected-container">
          <button
            className="cta-button submit-gif-button"
            onClick={createGifAccount}
          >
            Do One-Time Initialization For GIF Program Account
          </button>
        </div>
      );
    } else {
      return (
        <div className="connected-container">
          <nav className="navbar fixed-top navbar-expand-lg">
            <div className="container-fluid">
              <p className="header">HELLO {shortenAddress(walletAddress)}</p>
              <button
                className="cta-button disconnect-wallet-button"
                onClick={disconnectWallet}
              >
                SIGN OUT
              </button>
            </div>
          </nav>
          <nav className="navbar form-inline fixed-bottom">
            <form
              className="form"
              onSubmit={(event) => {
                event.preventDefault();
                sendGif();
              }}
            >
              <input
                type="text"
                placeholder="enter in your loyalty card number"
                value={inputValue}
                onChange={onInputChange}
              />
              <button type="submit" className="cta-button submit-gif-button">
                SUBMIT
              </button>
            </form>
          </nav>
          <Carousel>
            {gifList.map((item, index) => (
              <Card
                title={"CARD " + (index + 1)}
                content={
                  <div key={index}>
                    <Barcode
                      width={1}
                      fontSize={13}
                      format={"CODE128"}
                      value={item.gifLink}
                    />
                  </div>
                }
              />
            ))}
          </Carousel>
        </div>
      );
    }
  };

  //useEFFECTS

  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener("load", onLoad);
    return () => window.removeEventListener("load", onLoad);
  }, []);

  useEffect(() => {
    if (walletAddress) {
      console.log("Fetching GIF list...");

      // Call Solana program here.
      getGifList();
    }
  }, [walletAddress]);

  return (
    <div className="App">
      <div className={walletAddress ? "authed-container" : "container"}>
        <Toaster
          toastOptions={{
            className: "",
            duration: 3000,
            style: {
              border: "1px solid #713200",
              padding: "16px",
              color: "#713200",
            },
          }}
        />
        <div className="header-container">
          {!walletAddress && renderNotConnectedContainer()}
          {walletAddress && renderConnectedContainer()}
        </div>
      </div>
    </div>
  );
};

export default App;
